        <script src="//code.jquery.com/jquery-1.11.0.min.js"></script>
        <script>
            <?php
            if (!isset($__)) {
                $__ = function($line, $vars = array()) {
                    $lang = array();
                    require APPPATH."language/english/pancake_lang.php";

                    $line = $lang[$line];

                    for ($i = 0; $i < count($vars); $i++) {
                        $line = str_replace(':' . ($i + 1), $vars[$i], $line);
                    }

                    return $line;
                };
            }
            ?>
            $(".btn").on("click", function (event) {
                event.preventDefault();
                var $btn = $(this);
                if (!$btn.is(".success") && !$btn.is(".waiting")) {
                    $btn.addClass("waiting").html("<?php echo $__("error:sending_details"); ?>");
                    $.getJSON($btn.attr("href")).done(function (data) {
                        if (data.success) {
                            if (typeof(data.email) !== "undefined") {
                                $("<p style='margin-top: 1em;'><?php echo $__("error:response_will_be_sent_to_email"); ?></p>".split("{email}").join(data.email)).insertAfter($btn);
                            } else if (typeof(data.version) !== "undefined") {
                                $("<p style='margin-top: 1em;'><?php echo $__("error:fixed_in_version"); ?></p>".split("{version}").join(data.version)).insertAfter($btn);
                            } else {
                                $("<p style='margin-top: 1em;'><?php echo $__("error:already_being_dealt_with"); ?></p>").insertAfter($btn);
                            }
                            $btn.removeClass("waiting").addClass("success").html("&#x2713;");
                        } else {
                            alert(data.error);
                        }
                    }).fail(function () {
                        alert("<?php echo $__("error:unknown_error_reporting"); ?>");
                    });
                }
            });

        </script>
    </body>
